import React from "react";
import { Container, Table } from "react-bootstrap";
import "../../Assets/CitasPaciente.css";
import { data } from "../../data/dataCitasMedico";
import { Link } from "react-router-dom";


let datos = JSON.parse(JSON.stringify(data));

const Fecha = (props) => {
  return (
    <div >
    <div className='container mt-3 '>
        <h1>Tabla de citas del paciente</h1>
        <table id='citas'>
            <tr>
                <th>Fecha</th>
                <th>Hora</th>
                <th>Registrar presencia</th>
            </tr>

            {citas.map((data,index) => (
            
            <tr>
                <td>{data.fecha}</td>
                <td>{data.hora}</td>
                <td>
                    <button 
                        className='btn m-2 bg-success'
                        onClick={() => navigate(`/paciente/codigo/${citas[index].id}`)}                      
                        
                    >
                        <FontAwesomeIcon className='btn btn-success' icon={faCheck}/>

                    </button>
                
                </td>
            </tr>  
            ))}

        </table>
    </div>
</div>
    );
  };

  const Fechas = () => {
    return datos.map((arrDatos, idx) => {
      return (
        <Fecha
          key={idx}
          paciente={arrDatos[0]}
          dia={arrDatos[1]}
          mes={arrDatos[2]}
          hora={arrDatos[3]}
          
        />
      );
    });
  };

export const CitasMedico = (props) => {
  return (
    <Container>
      <Table responsive cellPadding="20">
        <thead>
          <Fechas />
        </thead>
        <tbody></tbody>
      </Table>
    </Container>
  );
};
export default CitasMedico;
