import React, { Component, useState, useEffect } from "react";
import { Container, Table } from "react-bootstrap";
import "../../Assets/CitasMedico.css";
import { data } from "../../data/dataCitasMedico";
import { Link, useParams } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSignOut, faCirclePlus, faBedPulse, faClockFour } from "@fortawesome/free-solid-svg-icons";


//let datos = JSON.parse(JSON.stringify(data));

export default function CitasMedico() {
  const [citas, setCitas] = useState([]);
  const [paciente, setPaciente] = useState([]);
  let {medico} = useParams();
  console.log(medico)

  async function remove(id) {
    await fetch(`http://localhost:8080/medico/${id}`, {
        method: 'DELETE',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
        }
    }).then(() => {
        let updatedCitas = [...this.citas].filter(i => i.id !== id);
        this.setCitas(updatedCitas);
    });
  }
  useEffect(() => {
    fetch(`http://localhost:8080/medico/${medico}`)
      .then((response) => response.json())
      .then((response) => setCitas(response));
    console.log(citas);
  }, [500]);
  console.log(`http://localhost:8080/medico/${medico}`)
  console.log(citas);


  return (
    <div id="contenedor">
      <div>
      <div class="btn-group btn-group-lg" role="group" aria-label="...">
      </div>
      </div>
      <div className="container mt-3 ">
        <div>
          <h1>Tabla de citas del m√©dico</h1>
          <div id="botones" className="btn-group btn-group-lg">
            <Link className="btn btn-success m-2 rounded-0" to="crear" id="a√±adir">
              Nueva cita &nbsp;
              <FontAwesomeIcon icon={faCirclePlus}/>

            </Link>

            <Link className="btn btn-success m-2 rounded-0" to="pacientes" id="a√±adir">
              Lista pacientes &nbsp;
              <FontAwesomeIcon icon={faBedPulse} />
            </Link>

          </div>
        </div>
        <table id="citas" className="mt-2">
          <tr>
            <th>Nombre</th>
            <th>
            <FontAwesomeIcon icon={faClockFour} />
            </th>
            <th>Fecha</th>
            <th></th>
            <th></th>
          </tr>

          {citas.map((data, index) => (
            <tr>
              <td>{data.nombrePaciente}</td> 
              <td>{data.hora}</td>
              <td>{data.fecha}</td>
              <td>
                <input type="button" value="Llamar paciente" onClick={change()} className="btn btn-success" to="codigo"/>
              </td>
              <td>
                <Link className="btn btn-success" to="codigo" id="eliminar" onClick={() => this.remove(data.id)}>
                  X
                </Link>
              </td>
            </tr>
          ))}
        </table>

        <Link className="btn btn-success m-2 rounded-0" to="../medico" id="salir">
          Cerrar sesi√≥n &nbsp; 

          <FontAwesomeIcon icon={faSignOut}/>
        </Link>




      </div>
    </div>
  );
}
