import React, { Component } from 'react'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useNavigate } from "react-router-dom";
import {faCircleLeft, faUser, faHashtag, faIdCard, faFilePen, faClock, faCalendar } from '@fortawesome/free-solid-svg-icons'
import axios from 'axios';

export default class CreateCitaComponent extends Component {
    


    constructor(props) {
        super(props)

        this.state = {
            id: '90',
            fecha:'',
            hora:'',
            llamado:'false',
            registrado: 'false',
            medico:'',
            dni: '',
            ticketTurno: 'null',
            razon: '',
            sala_consulta: '',
            nombrePaciente: ''
        }
        this.changeIdNameHandler = this.changeIdHandler.bind(this);
        this.changeFechaHandler = this.changeFechaHandler.bind(this);
        this.changeHoraHandler = this.changeHoraHandler.bind(this);
        this.changeMedicoHandler = this.changeMedicoHandler.bind(this);
        this.changeDniHandler = this.changeDniHandler.bind(this);
        this.changeRazonHandler = this.changeRazonHandler.bind(this);
        this.changePacienteHandler = this.changePacienteHandler.bind(this);

        this.saveCita = this.saveCita.bind(this);

    }

    
    saveCita = () => {

        let Cita = {id: this.state.id, fecha: this.state.fecha, hora: this.state.hora, llamado: this.state.llamado, registrado: this.state.registrado, medico: this.state.medico, dni: this.state.dni, ticketTurno: this.state.ticketTurno, razon: this.state.razon, sala_consulta: this.state.sala_consulta, nombrePaciente: this.state.nombrePaciente};
        
        axios.post("http://localhost:8080/medico/crear", Cita);
            
    } 
    /*     e.preventDefault();
        let Cita = {id: this.state.id, fecha: this.state.fecha, hora: this.state.hora, llamado: this.state.llamado, registrado: this.state.registrado, medico: this.state.medico, dni: this.state.dni, ticketTurno: this.state.ticketTurno, razon: this.state.razon, sala_consulta: this.state.sala_consulta, nombrePaciente: this.state.nombrePaciente};

        console.log('Cita => ' + JSON.stringify(Cita));

        // step 5
        
            CitaService.createCita(Cita).then(res =>{
                this.props.history.push('/Citas');
            });
         */
        
  
    
    changeIdHandler= (event) => {
        this.setState({id: event.target.value});
    }

    changeFechaHandler= (event) => {
        this.setState({fecha: event.target.value});
    }

    changeHoraHandler= (event) => {
        this.setState({hora: event.target.value});
    }

    changeMedicoHandler= (event) => {
        this.setState({medico: event.target.value});
    }

    changeDniHandler= (event) => {
        this.setState({dni: event.target.value});
    }

    changeRazonHandler= (event) => {
        this.setState({razon: event.target.value});
    }
    
    changePacienteHandler= (event) => {
        this.setState({nombrePaciente: event.target.value});
    }

    render() {
        
        return (
            <div className="contenedor">
    
            <div >
            
                <h1>Añadir nueva cita</h1>
            </div>     
        {/* <FontAwesomeIcon className='btn-success bg-success rounded-circle text-white mt-3 mb-2 border border-3 border-success ' icon={faCircleLeft} size="2x" onClick={() => navigate(`/medico/${colegiado}`)} /> */}

            <form>
                <div className="form-row align-items-center">
                
                <div className="col-auto">
                    <label className="sr-only" htmlFor="inlineFormInputGroup">TicketID</label>
                    <div className="input-group mb-2">
                    <div className="input-group-prepend">
                        <div className="input-group-text">
                        <FontAwesomeIcon icon={faHashtag} size="2x"/>

                        </div>
                    </div>
                    <input type="text" className="ml-1 form-control" id="inlineFormInputGroup" placeholder="Id de la cita" value={this.state.id} onChange={this.changeIdHandler}/>
                    </div>
                </div>
                
                <div className="col-auto">
                    <label className="sr-only" htmlFor="inlineFormInputGroup">Paciente</label>
                    <div className="input-group mb-2">
                    <div className="input-group-prepend">
                        <div className="input-group-text">      
                        <FontAwesomeIcon icon={faUser} size="2x"/>
                        </div>
                    </div>
                    <input type="text" className="ml-1 form-control" id="inlineFormInputGroup" placeholder="Nombre paciente" value={this.state.nombrePaciente} onChange={this.changePacienteHandler}/>
                    </div>
                </div>

                <div className="col-auto">
                <label className="sr-only" htmlFor="inlineFormInputGroup">DNI</label>
                <div className="input-group mb-2">
                    <div className="input-group-prepend">
                    <div className="input-group-text">      
                        <FontAwesomeIcon icon={faIdCard} size="2x"/>
                    </div>
                    </div>
                    <input type="text" className="ml-1 form-control" id="inlineFormInputGroup" placeholder="Número de DNI/NIF" value={this.state.dni} onChange={this.changeDniHandler}/>
                </div>
                </div>

                <div className="col-auto">
                <label className="sr-only" htmlFor="inlineFormInputGroup">RAZÓN</label>
                <div className="input-group mb-2">
                    <div className="input-group-prepend">
                    <div className="input-group-text">      
                        <FontAwesomeIcon icon={faFilePen} size="2x"/>
                    </div>
                    </div>
                    <input type="text" className="ml-1 form-control" id="inlineFormInputGroup" placeholder="Razón de consulta" value={this.state.razon} onChange={this.changeRazonHandler}/>
                </div>
                </div>

                <div className="col-auto">
                <label className="sr-only" htmlFor="inlineFormInputGroup">HORA</label>
                <div className="input-group mb-2">
                    <div className="input-group-prepend">
                    <div className="input-group-text">      
                        <FontAwesomeIcon icon={faClock} size="2x"/>
                    </div>
                    </div>
                    <input type="time" className="ml-1 form-control" id="inlineFormInputGroup" placeholder="Hora" value={this.state.hora} onChange={this.changeHoraHandler}/>
                </div>
                </div>

                <div className="col-auto">
                <label className="sr-only" htmlFor="inlineFormInputGroup">FECHA</label>
                <div className="input-group mb-2">
                    <div className="input-group-prepend">
                    <div className="input-group-text">      
                        <FontAwesomeIcon icon={faCalendar} size="2x"/>
                    </div>
                    </div>
                    <input type="date" className="ml-1 form-control" id="inlineFormInputGroup" placeholder="Fecha" value={this.state.fecha} onChange={this.changeFechaHandler}/>
                </div>
                </div>
                
            </div>
            <div className="col-auto">
                <button onClick={this.saveCita} className="btn btn-success mb-2">Añadir cita</button>
            </div>
            </form>  
        
        
            </div>
           
        )
    }
}
