import React from "react";
import { Container, Table } from "react-bootstrap";
import "../../Assets/CitasMedico.css";
import { data } from "../../data/dataCitasMedico";
import { Link } from "react-router-dom";

let datos = JSON.parse(JSON.stringify(data));

const Fecha = (props) => {
  return (
    <div style={{ backgroundColor: "white" }}>
      <div className="container">
        <div className="row">
          <div className="col-12">
            <table id="citas">
              <tr>
                <th scope="col">Nombre</th>
                <th scope="col">Hora</th>
                <th scope="col">Fecha</th>
                <th scope="col">Llamar</th>
              </tr>
              <tr>
                <td scope="row">{props.paciente}</td>
                <td scope="row" id="hora">
                  {props.fecha}
                </td>
                <td scope="row" id="doctor">
                  {props.hora}
                </td>
                <td scope="row" id="asistir">
                  <Link className="btn btn-success" to="codigo">
                    Llamar paciente
                  </Link>
                </td>
              </tr>
            </table>
          </div>
        </div>
      </div>
    </div>
    //     <div >
    //     <div className='container mt-3 '>
    //         <h1>Tabla de citas del médico</h1>
    //         <table id='citas'>
    //             <tr>
    //                 <th>Nombre</th>
    //                 <th>Fecha</th>
    //                 <th>Hora</th>
    //                 <th>Llamar paciente</th>
    //             </tr>

    //             {citas.map((data,index) => (

    //             <tr>
    //                 <td>{data.fecha}</td>
    //                 <td>{data.hora}</td>
    //                 <td>
    //                     <button
    //                         className='btn m-2 bg-success'
    //                         onClick={() => navigate(`/paciente/codigo/${citas[index].id}`)}

    //                     >
    //                         <FontAwesomeIcon className='btn btn-success' icon={faCheck}/>

    //                     </button>

    //                 </td>
    //             </tr>
    //             ))}

    //         </table>
    //     </div>
    // </div>
  );
};

const Fechas = () => {
  return datos.map((arrDatos, idx) => {
    return (
      <Fecha
        key={idx}
        paciente={arrDatos[0]}
        hora={arrDatos[1]}
        fecha={arrDatos[2]}
      />
    );
  });
};

export const CitasMedico = (props) => {
  return (
    <Container>
      <Table responsive cellPadding="20">
        <thead>
          <Fechas 
          key={idx}
          paciente={arrDatos[0]}
          hora={arrDatos[1]}
          fecha={arrDatos[2]}/>
        </thead>
        <tbody></tbody>
      </Table>
    </Container>
  );
};
export default CitasMedico;
