import React, {useState} from "react";
import "../../Assets/Paciente/Login.css";
import {Link} from 'react-router-dom'

export default class Inputdni extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      dni:``
    };
  }

  componentDidMount (){
    /*...*/
  }

  render() {
    return <div>
      <form>
      <div className="btn-group-vertical">
        <input
        className="form-control"
        placeholder="Introduzca su DNI"
        pattern="[0-9]{8}[A-Z]{1}"
        title="Debe poner 8 números y una letra"
        value={dni}   
        name="" 
        onChange={this.setState(e.target.value.toUpperCase())} 
        type="text" 
        />
      
        <button className="mt-2 btn btn-success" >
        <a 
        className="text-light"
        style={{textDecoration: 'none'}}
        href={"paciente/" + dni}>
        Mostrar citas
        </a>
        

        </button>
      </div>
    </form>       

    
    
    </div>
  }


}

